def rand_add():
    o = input("Enter a number: ")
    p = input("Enter another number: ")
    o = int(o)
    p = int(p)
    print("The added no. of", o," and", p, "is :" ,o+p)
    q = o+p
    randno = random.randint(0, q)
    print("The random number is :" ,randno)

