def multi_substract():
    at = input("Enter a number: ")
    au = input("Enter another number: ")
    av = input("Enter another number: ")
    at = int(at)
    au = int(au)
    av = int(av)
    print("The substracted no. of", at,",", au ,"and", av, "is :" ,at-au-av)
